mapper.select:
select
	apply_id,name,sex,address,id_number,apply_money,phone,verify_code,merchant_id,sales_man_phone,apply_time,loan_time,loan_money,is_overdue,overdue_money,state,brokerage_rate,brokerage,service_rate,service_charge,deposite_rate,break_time,break_money,merchant_name,merchant_type,is_assurance
from apply

	 AND apply_id = #{apply.applyId}
	 AND name = #{apply.name}
	 AND sex = #{apply.sex}
	 AND address = #{apply.address}
	 AND id_number = #{apply.idNumber}
	 AND apply_money = #{apply.applyMoney}
	 AND phone = #{apply.phone}
	 AND verify_code = #{apply.verifyCode}
	 AND merchant_id = #{apply.merchantId}
	 AND sales_man_phone = #{apply.salesManPhone}
	 AND apply_time = #{apply.applyTime}
	 AND loan_time = #{apply.loanTime}
	 AND loan_money = #{apply.loanMoney}
	 AND is_overdue = #{apply.isOverdue}
	 AND overdue_money = #{apply.overdueMoney}
	 AND state = #{apply.state}
	 AND brokerage_rate = #{apply.brokerageRate}
	 AND brokerage = #{apply.brokerage}
	 AND service_rate = #{apply.serviceRate}
	 AND service_charge = #{apply.serviceCharge}
	 AND deposite_rate = #{apply.depositeRate}
	 AND break_time = #{apply.breakTime}
	 AND break_money = #{apply.breakMoney}
	 AND merchant_name = #{apply.merchantName}
	 AND merchant_type = #{apply.merchantType}
	 AND is_assurance = #{apply.isAssurance}

select
	apply_id,name,sex,address,id_number,apply_money,phone,verify_code,merchant_id,sales_man_phone,apply_time,loan_time,loan_money,is_overdue,overdue_money,state,brokerage_rate,brokerage,service_rate,service_charge,deposite_rate,break_time,break_money,merchant_name,merchant_type,is_assurance
from apply

	AND id_number=#{idNumber}
	AND apply_time=#{applyTime}
	AND phone=#{phone}

select
    apply_id,name,sex,address,id_number,apply_money,phone,verify_code,merchant_id,sales_man_phone,apply_time,loan_time,loan_money,is_overdue,overdue_money,state,brokerage_rate,brokerage,service_rate,service_charge,deposite_rate,break_time,break_money,merchant_name,merchant_type,is_assurance
from apply

     AND phone = #{userPhone}
     AND state = #{state}
     AND name = #{name}
     AND merchant_name = #{merchantName}
     AND merchant_type = #{merchantType}
    AND apply_time >= #{startTime}
    AND apply_time <= #{endTime}
    AND is_assurance <= #{isAssurance}
    AND phone= #{customerPhone} or customer_manager_phone=#{customerPhone}
