mapper.select:
select
	merchant_id,merchant_name,merchant_type,merchant_address,is_assurance,deposite_amount,deposite_rate,deposite_balance,is_shared,shared_rate,brokerage_rate,brokerage_amount,brokerage_less,apply_amount,apply_money_amount,loan_amount,loan_money_amount,break_amount,break_money_amount,partner_time,stop_time,state,customer_manager_phone,customer_manager_name,provide_deposit
from merchant

	 AND merchant_id = #{merchant.merchantId}
	 AND merchant_name = #{merchant.merchantName}
	 AND merchant_type = #{merchant.merchantType}
	 AND merchant_address = #{merchant.merchantAddress}
	 AND is_assurance = #{merchant.isAssurance}
	 AND deposite_amount = #{merchant.depositeAmount}
	 AND deposite_rate = #{merchant.depositeRate}
	 AND deposite_balance = #{merchant.depositeBalance}
	 AND is_shared = #{merchant.isShared}
	 AND shared_rate = #{merchant.sharedRate}
	 AND brokerage_rate = #{merchant.brokerageRate}
	 AND brokerage_amount = #{merchant.brokerageAmount}
	 AND brokerage_less = #{merchant.brokerageLess}
	 AND apply_amount = #{merchant.applyAmount}
	 AND apply_money_amount = #{merchant.applyMoneyAmount}
	 AND loan_amount = #{merchant.loanAmount}
	 AND loan_money_amount = #{merchant.loanMoneyAmount}
	 AND break_amount = #{merchant.breakAmount}
	 AND break_money_amount = #{merchant.breakMoneyAmount}
	 AND partner_time = #{merchant.partnerTime}
	 AND stop_time = #{merchant.stopTime}
	 AND state = #{merchant.state}
	 AND customer_manager_phone = #{merchant.customerManagerPhone}
	 AND customer_manager_name = #{merchant.customerManagerName}
	 AND provide_deposit = #{merchant.provideDeposit}

select
	merchant_id,merchant_name,merchant_type,merchant_address,is_assurance,deposite_amount,deposite_rate,deposite_balance,is_shared,shared_rate,brokerage_rate,brokerage_amount,brokerage_less,apply_amount,apply_money_amount,loan_amount,loan_money_amount,break_amount,break_money_amount,partner_time,stop_time,state,customer_manager_phone,customer_manager_name,provide_deposit
from merchant

	AND merchant_name=#{merchantName}
	AND merchant_address=#{merchantAddress}
	AND merchant_type=#{merchantType}

select
	merchant_id,merchant_name,merchant_type,merchant_address,is_assurance,deposite_amount,deposite_rate,deposite_balance,is_shared,shared_rate,brokerage_rate,brokerage_amount,brokerage_less,apply_amount,apply_money_amount,loan_amount,loan_money_amount,break_amount,break_money_amount,partner_time,stop_time,state,customer_manager_phone,customer_manager_name,provide_deposit
from merchant


		 AND state = #{state}
		 AND merchant_name = #{merchantName}
		 AND merchant_type = #{merchantType}
		 AND is_assurance = #{isAssurance}
		 AND partner_time >= #{startTime}
		 AND partner_time <= #{endTime}
		 AND customer_manager_phone = #{customerManagerPhone}

