		 AND merchant_type = #{merchantType}
		 AND is_assurance = #{isAssurance}
		 AND partner_time >= #{startTime}
		 AND partner_time <= #{endTime}
		 AND customer_manager_phone = #{customerManagerPhone}

select
	merchant_id,merchant_name,merchant_type,merchant_address,is_assurance,deposite_amount,deposite_rate,deposite_balance,is_shared,shared_rate,brokerage_rate,brokerage_amount,brokerage_less,apply_amount,apply_money_amount,loan_amount,loan_money_amount,break_amount,break_money_amount,partner_time,stop_time,state,customer_manager_phone,customer_manager_name,provide_deposit
from merchant

	 AND merchant_id = #{merchantId}
	 AND merchant_name = #{merchantName}
	 AND merchant_type = #{merchantType}
	 AND merchant_address = #{merchantAddress}
	 AND is_assurance = #{isAssurance}
	 AND deposite_amount = #{depositeAmount}
	 AND deposite_rate = #{depositeRate}
	 AND deposite_balance = #{depositeBalance}
	 AND is_shared = #{isShared}
	 AND shared_rate = #{sharedRate}
	 AND brokerage_rate = #{brokerageRate}
	 AND brokerage_amount = #{brokerageAmount}
	 AND brokerage_less = #{brokerageLess}
	 AND apply_amount = #{applyAmount}
	 AND apply_money_amount = #{applyMoneyAmount}
	 AND loan_amount = #{loanAmount}
	 AND loan_money_amount = #{loanMoneyAmount}
	 AND break_amount = #{breakAmount}
	 AND break_money_amount = #{breakMoneyAmount}
	 AND partner_time = #{partnerTime}
	 AND stop_time = #{stopTime}
	 AND state = #{state}
	 AND customer_manager_phone = #{customerManagerPhone}
	 AND customer_manager_name = #{customerManagerName}
	 AND partner_time >= #{startTime}
	 AND partner_time <= #{endTime}

