mapper.delete:
delete from system_account

	 and account_id = #{accountId}  
	 and brokerage = #{brokerage}  
	 and provisionsOfRisk = #{provisionsOfRisk}  
	 and serviceCharge = #{serviceCharge}

mapper.update:
update system_account

	account_id=#{systemAccount.accountId},
	brokerage=#{systemAccount.brokerage},
	provisionsOfRisk=#{systemAccount.provisionsOfRisk},
	serviceCharge=#{systemAccount.serviceCharge}


	 AND account_id = #{param.accountId}
	 AND brokerage = #{param.brokerage}
	 AND provisionsOfRisk = #{param.provisionsOfRisk}
	 AND serviceCharge = #{param.serviceCharge}

update system_account

	account_id=#{systemAccount.accountId},
	brokerage=#{systemAccount.brokerage},
	provisionsOfRisk=#{systemAccount.provisionsOfRisk},
	serviceCharge=#{systemAccount.serviceCharge}


	AND account_id=#{accountId}

mapper.select:
select
	account_id,brokerage,provisionsOfRisk,serviceCharge
from system_account

	 AND account_id = #{accountId}
	 AND brokerage = #{brokerage}
	 AND provisionsOfRisk = #{provisionsOfRisk}
	 AND serviceCharge = #{serviceCharge}
