mapper.delete:
delete from system_account

	 and account_id = #{accountId}  
	 and brokerage = #{brokerage}  
	 and provisions_of_risk = #{provisionsOfRisk}  
	 and service_charge = #{serviceCharge}  
	 and brokerage_rate = #{brokerageRate}  
	 and provisions_of_risk_rate = #{provisionsOfRiskRate}  
	 and service_charge_rate = #{serviceChargeRate}  
	 and provisions_of_risk_max = #{provisionsOfRiskMax}

mapper.update:
update system_account

	account_id=#{systemAccount.accountId},
	brokerage=#{systemAccount.brokerage},
	provisions_of_risk=#{systemAccount.provisionsOfRisk},
	service_charge=#{systemAccount.serviceCharge},
	brokerage_rate=#{systemAccount.brokerageRate},
	provisions_of_risk_rate=#{systemAccount.provisionsOfRiskRate},
	service_charge_rate=#{systemAccount.serviceChargeRate},
	provisions_of_risk_max=#{systemAccount.provisionsOfRiskMax}


	 AND account_id = #{param.accountId}
	 AND brokerage = #{param.brokerage}
	 AND provisions_of_risk = #{param.provisionsOfRisk}
	 AND service_charge = #{param.serviceCharge}
	 AND brokerage_rate = #{param.brokerageRate}
	 AND provisions_of_risk_rate = #{param.provisionsOfRiskRate}
	 AND service_charge_rate = #{param.serviceChargeRate}
	 AND provisions_of_risk_max = #{param.provisionsOfRiskMax}

update system_account
account_id=#{systemAccount.accountId}, brokerage=#{systemAccount.brokerage}, provisions_of_risk=#{systemAccount.provisionsOfRisk}, service_charge=#{systemAccount.serviceCharge}, brokerage_rate=#{systemAccount.brokerageRate}, provisions_of_risk_rate=#{systemAccount.provisionsOfRiskRate}, service_charge_rate=#{systemAccount.serviceChargeRate}, provisions_of_risk_max=#{systemAccount.provisionsOfRiskMax}
AND account_id=#{accountId}

mapper.select:
select
	account_id,brokerage,provisions_of_risk,service_charge,brokerage_rate,provisions_of_risk_rate,service_charge_rate,provisions_of_risk_max
from system_account

	 AND account_id = #{accountId}
	 AND brokerage = #{brokerage}
	 AND provisions_of_risk = #{provisionsOfRisk}
	 AND service_charge = #{serviceCharge}
	 AND brokerage_rate = #{brokerageRate}
	 AND provisions_of_risk_rate = #{provisionsOfRiskRate}
	 AND service_charge_rate = #{serviceChargeRate}
	 AND provisions_of_risk_max = #{provisionsOfRiskMax}
