mapper.delete:
delete from system_account_record

	 and nano_time = #{nanoTime}  
	 and account_id = #{accountId}  
	 and is_add = #{isAdd}  
	 and monety_change = #{monetyChange}  
	 and record_time = #{recordTime}  
	 and money_type = #{moneyType}  
	 and reason = #{reason}

mapper.update:
update system_account_record

	nano_time=#{systemAccountRecord.nanoTime},
	account_id=#{systemAccountRecord.accountId},
	is_add=#{systemAccountRecord.isAdd},
	monety_change=#{systemAccountRecord.monetyChange},
	record_time=#{systemAccountRecord.recordTime},
	money_type=#{systemAccountRecord.moneyType},
	reason=#{systemAccountRecord.reason}


	 AND nano_time = #{param.nanoTime}
	 AND account_id = #{param.accountId}
	 AND is_add = #{param.isAdd}
	 AND monety_change = #{param.monetyChange}
	 AND record_time = #{param.recordTime}
	 AND money_type = #{param.moneyType}
	 AND reason = #{param.reason}

update system_account_record
nano_time=#{systemAccountRecord.nanoTime}, account_id=#{systemAccountRecord.accountId}, is_add=#{systemAccountRecord.isAdd}, monety_change=#{systemAccountRecord.monetyChange}, record_time=#{systemAccountRecord.recordTime}, money_type=#{systemAccountRecord.moneyType}, reason=#{systemAccountRecord.reason}
AND nano_time=#{nanoTime}

mapper.select:
select
	nano_time,account_id,is_add,monety_change,record_time,money_type,reason
from system_account_record

	 AND nano_time = #{nanoTime}
	 AND account_id = #{accountId}
	 AND is_add = #{isAdd}
	 AND monety_change = #{monetyChange}
	 AND record_time = #{recordTime}
	 AND money_type = #{moneyType}
	 AND reason = #{reason}
