mapper.delete:
delete from merchant_account_record

	 and nano_time = #{nanoTime}  
	 and merchant_id = #{merchantId}  
	 and is_add = #{isAdd}  
	 and monety_change = #{monetyChange}  
	 and record_time = #{recordTime}  
	 and money_type = #{moneyType}  
	 and reason = #{reason}

mapper.update:
update merchant_account_record

	nano_time=#{merchantAccountRecord.nanoTime},
	merchant_id=#{merchantAccountRecord.merchantId},
	is_add=#{merchantAccountRecord.isAdd},
	monety_change=#{merchantAccountRecord.monetyChange},
	record_time=#{merchantAccountRecord.recordTime},
	money_type=#{merchantAccountRecord.moneyType},
	reason=#{merchantAccountRecord.reason}


	 AND nano_time = #{param.nanoTime}
	 AND merchant_id = #{param.merchantId}
	 AND is_add = #{param.isAdd}
	 AND monety_change = #{param.monetyChange}
	 AND record_time = #{param.recordTime}
	 AND money_type = #{param.moneyType}
	 AND reason = #{param.reason}

update merchant_account_record
nano_time=#{merchantAccountRecord.nanoTime}, merchant_id=#{merchantAccountRecord.merchantId}, is_add=#{merchantAccountRecord.isAdd}, monety_change=#{merchantAccountRecord.monetyChange}, record_time=#{merchantAccountRecord.recordTime}, money_type=#{merchantAccountRecord.moneyType}, reason=#{merchantAccountRecord.reason}
AND nano_time=#{nanoTime}

mapper.select:
select
	nano_time,merchant_id,is_add,monety_change,record_time,money_type,reason
from merchant_account_record

	 AND nano_time = #{nanoTime}
	 AND merchant_id = #{merchantId}
	 AND is_add = #{isAdd}
	 AND monety_change = #{monetyChange}
	 AND record_time = #{recordTime}
	 AND money_type = #{moneyType}
	 AND reason = #{reason}
